sp_widget.client_script:
(function() {
    'use strict';

    var c = this;

    // Initialize widget
    c.$onInit = function() {
        c.loading = true;
        c.badges = [];
        c.audience = c.options.default_audience || 'all';

        // Load initial data
        c.server.get({
            action: 'getBadges',
            audience: c.audience
        }).then(function(response) {
            c.badges = response.data.badges || [];
            c.loading = false;
        }).catch(function(error) {
            console.error('Error loading badges:', error);
            c.loading = false;
            spUtil.addErrorMessage('Failed to load achievements');
        });
    };

    // Prioritize badges using AI
    c.prioritizeBadges = function() {
        if (c.loading) return;

        c.loading = true;

        c.server.get({
            action: 'prioritizeBadges',
            audience: c.audience,
            badges: c.badges
        }).then(function(response) {
            c.badges = response.data.prioritizedBadges || [];
            c.aiConfidence = response.data.confidence || 0.75;
            c.loading = false;

            // Show success message
            spUtil.addInfoMessage('AI prioritization completed for ' + c.getAudienceLabel(c.audience) + ' audience');
        }).catch(function(error) {
            console.error('Error prioritizing badges:', error);
            spUtil.addErrorMessage('Failed to prioritize badges: ' + (error.message || 'Unknown error'));
            c.loading = false;
        });
    };

    // Filter badges by audience
    c.onAudienceChange = function() {
        if (c.badges.length > 0) {
            c.prioritizeBadges();
        }
    };

    // Get priority class for display
    c.getPriorityClass = function(score) {
        if (score >= 90) return 'high';
        if (score >= 70) return 'medium';
        return 'low';
    };

    // Format date for display
    c.formatDate = function(dateString) {
        if (!dateString) return '';
        return new Date(dateString).toLocaleDateString();
    };

    // Get audience label
    c.getAudienceLabel = function(audience) {
        var labels = {
            'all': 'All Audiences',
            'it_recruiters': 'IT Recruiters',
            'veteran_community': 'Veteran Community',
            'servicenow_professionals': 'ServiceNow Professionals'
        };
        return labels[audience] || audience;
    };

    // Show badge details
    c.showBadgeDetails = function(badge) {
        var message = 'Achievement Details:\n\n' +
                     'Name: ' + badge.name + '\n' +
                     'Issuer: ' + badge.issuer + '\n' +
                     'Type: ' + badge.type + '\n' +
                     'Date Earned: ' + c.formatDate(badge.date_earned) + '\n' +
                     'Priority Score: ' + badge.priority_score + '\n' +
                     'Category: ' + badge.category;

        if (badge.ai_reasoning) {
            message += '\n\nAI Reasoning: ' + badge.ai_reasoning;
        }

        spModal.alert(message);
    };
})();

