sp_widget.script:
(function() {
    'use strict';

    // Handle client requests
    if (input && input.action) {
        switch (input.action) {
            case 'getBadges':
                data.badges = getBadges(input.audience || 'all');
                break;

            case 'prioritizeBadges':
                var result = prioritizeBadges(input.badges || [], input.audience || 'all');
                data.prioritizedBadges = result.badges;
                data.confidence = result.confidence;
                break;
        }
    }

    // Initialize widget options
    data.title = options.title || "üéñÔ∏è Achievement Badges";
    data.maxBadges = parseInt(options.max_badges) || 10;
    data.showAIInsights = (options.show_ai_insights === 'true');
    data.defaultAudience = options.default_audience || 'it_recruiters';
    data.enableDetails = (options.enable_details === 'true');

    function getBadges(audience) {
        var badges = [];

        try {
            // Query achievement table
            var gr = new GlideRecord('x_snc_snas_port_achievement');
            gr.addActiveQuery();
            gr.orderByDesc('date_earned');
            if (data.maxBadges > 0) {
                gr.setLimit(data.maxBadges);
            }
            gr.query();

            while (gr.next()) {
                var badge = {
                    id: gr.getUniqueValue(),
                    name: gr.getValue('name') || '',
                    issuer: gr.getValue('issuer') || '',
                    type: gr.getValue('type') || '',
                    date_earned: gr.getValue('date_earned') || '',
                    description: gr.getValue('description') || '',
                    category: gr.getValue('category') || '',
                    priority_score: parseInt(gr.getValue('priority_score')) || 50
                };

                if (data.showAIInsights && gr.getValue('ai_reasoning')) {
                    badge.ai_reasoning = gr.getValue('ai_reasoning');
                }

                badges.push(badge);
            }

            // Apply audience-specific filtering if needed
            if (audience !== 'all') {
                badges = filterByAudience(badges, audience);
            }

        } catch (e) {
            gs.error('[SNAS] Error loading badges: ' + e.message);
            // Return sample data as fallback
            badges = getSampleBadges();
        }

        return badges;
    }

    function prioritizeBadges(badges, audience) {
        try {
            // Try to use AI API if available
            var AchievementAPI = new global.AchievementAPI();
            if (AchievementAPI) {
                var prioritizationResult = AchievementAPI.prioritizeAchievements({
                    achievements: badges,
                    context: {
                        audience: audience,
                        include_reasoning: data.showAIInsights
                    }
                });

                return {
                    badges: prioritizationResult.prioritized_achievements || badges,
                    confidence: prioritizationResult.confidence || 0.75
                };
            }

        } catch (e) {
            gs.warn('[SNAS] AI API unavailable, using fallback prioritization: ' + e.message);
        }

        // Fallback prioritization logic
        return getFallbackPrioritization(badges, audience);
    }

    function getFallbackPrioritization(badges, audience) {
        var prioritizedBadges = badges.map(function(badge) {
            var score = badge.priority_score || 50;
            var reasoning = [];

            // Audience-specific boosts
            switch (audience) {
                case 'it_recruiters':
                    if (badge.name.toLowerCase().indexOf('csa') !== -1) {
                        score += 25;
                        reasoning.push('CSA certification priority for IT recruiters (+25)');
                    }
                    if (badge.type === 'certification') {
                        score += 15;
                        reasoning.push('Certification type boost (+15)');
                    }
                    break;

                case 'veteran_community':
                    if (badge.category === 'Leadership' || badge.category === 'Community Impact') {
                        score += 20;
                        reasoning.push('Veteran leadership relevance (+20)');
                    }
                    break;

                case 'servicenow_professionals':
                    if (badge.issuer === 'ServiceNow') {
                        score += 15;
                        reasoning.push('ServiceNow platform relevance (+15)');
                    }
                    break;
            }

            // Recency boost
            if (badge.date_earned) {
                var earnedDate = new Date(badge.date_earned);
                var daysSince = (new Date() - earnedDate) / (1000 * 60 * 60 * 24);
                if (daysSince <= 90) {
                    score += 20;
                    reasoning.push('Recent achievement boost (+20)');
                }
            }

            badge.priority_score = Math.min(100, score);
            if (data.showAIInsights && reasoning.length > 0) {
                badge.ai_reasoning = reasoning.join(', ');
            }

            return badge;
        });

        // Sort by priority score
        prioritizedBadges.sort(function(a, b) {
            return (b.priority_score || 50) - (a.priority_score || 50);
        });

        return {
            badges: prioritizedBadges,
            confidence: 0.80
        };
    }

    function filterByAudience(badges, audience) {
        switch (audience) {
            case 'it_recruiters':
                return badges.filter(function(badge) {
                    return badge.type === 'certification' || 
                           badge.name.toLowerCase().indexOf('csa') !== -1 ||
                           badge.category === 'Platform Administration';
                });

            case 'veteran_community':
                return badges.filter(function(badge) {
                    return badge.category === 'Leadership' || 
                           badge.category === 'Community Impact' ||
                           badge.issuer === 'U.S. Navy' ||
                           badge.issuer === 'U.S. Military';
                });

            case 'servicenow_professionals':
                return badges.filter(function(badge) {
                    return badge.issuer === 'ServiceNow';
                });

            default:
                return badges;
        }
    }

    function getSampleBadges() {
        return [
            {
                id: 'sample-csa',
                name: 'Certified System Administrator (CSA)',
                issuer: 'ServiceNow',
                type: 'certification',
                date_earned: '2024-08-15',
                description: 'Comprehensive ServiceNow platform administration certification demonstrating expertise in system configuration, user management, and platform maintenance.',
                category: 'Platform Administration',
                priority_score: 95
            },
            {
                id: 'sample-itsm',
                name: 'Certified Implementation Specialist - ITSM',
                issuer: 'ServiceNow',
                type: 'certification',
                date_earned: '2024-07-22',
                description: 'Advanced ITSM implementation certification covering incident, problem, change, and service catalog management.',
                category: 'ITSM Implementation',
                priority_score: 88
            },
            {
                id: 'sample-riseup',
                name: 'RiseUp Achievement Badge',
                issuer: 'ServiceNow',
                type: 'achievement',
                date_earned: '2024-09-10',
                description: 'Recognition for outstanding contribution to diversity and inclusion in technology through mentorship and community involvement.',
                category: 'Community Impact',
                priority_score: 82
            }
        ];
    }
})();
